static void Init_data(Gen_Second_Data * L)
{
	L->checkbox_sta		=	0;
	L->Item				=	First_Item;
	L->change_item		=	-1;
	L->Key_count		=	0;
	
	//复选框状态初始化,初次读取24CXX芯片的数据是0xFF，所以要初始化
	Checkbox_Init(&L->data.WHITE_AUX1_STA);
	Checkbox_Init(&L->data.WHITE_AUX2_STA);
	Checkbox_Init(&L->data.WHITE_AUX3_STA);
	Checkbox_Init(&L->data.WHITE_AUX4_STA);
	Checkbox_Init(&L->data.WHITE_AUX5_STA);
	Checkbox_Init(&L->data.WHITE_AUX6_STA);
	Checkbox_Init(&L->data.WHITE_AUX7_STA);
	Checkbox_Init(&L->data.WHITE_AUX8_STA);
	
	Checkbox_Init(&L->data.PINK_AUX1_STA);
	Checkbox_Init(&L->data.PINK_AUX2_STA);
	Checkbox_Init(&L->data.PINK_AUX3_STA);
	Checkbox_Init(&L->data.PINK_AUX4_STA);
	Checkbox_Init(&L->data.PINK_AUX5_STA);
	Checkbox_Init(&L->data.PINK_AUX6_STA);
	Checkbox_Init(&L->data.PINK_AUX7_STA);
	Checkbox_Init(&L->data.PINK_AUX8_STA);
}


WM_HWIN Gen_Second(void) {
	WM_HWIN hWin;
	
	Init_data(Gen_second);
	
	Current_Data = Gen_second;
	
	hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
	return hWin;
}

