void Value_Change_add_32(void *p,u16 *time)
{
	
	s32 *q = (s32 *)p;
	(*time)++;
	if ((*time) < 20)
	{
		(*q)++;
	}
	else if ((*time) < 40)
	{
		(*q) += 10;
	}
	else if ((*time) < 100)
	{
		(*q) += 100;
	}
	else if((*time) >= 100)
	{
		(*q) += 1000;
	}

}

void Value_Change_dec_32(void *p,u16 *time)
{
	
	s32 *q = (s32 *)p;
	(*time)++;
	if ((*time) < 20)
	{
		(*q)--;
	}
	else if ((*time) < 40)
	{
		(*q) -= 10;
	}
	else if ((*time) < 100)
	{
		(*q) -= 100;
	}
	else if((*time) >= 100)
	{
		(*q) -= 1000;
	}

}

void Time_add_dec_32(s32 *Value,const GUI_RECT * pRect, WM_HTIMER *time,u8 fight,WM_MESSAGE * pMsg)
{
	if((fight%2) ==  0)
	{
		(*Value)--;
	}
	else
	{
		(*Value)++;
	}
	WM_InvalidateRect(pMsg->hWin,pRect);
	(*time) = WM_CreateTimer(pMsg->hWin,fight,200,0);
}

void Time_long_press_32(WM_MESSAGE * pMsg,s32 *p,u16 *time,const GUI_RECT * pRect,u8 fight)
{
	if((fight%2) ==  0)
	{
		Value_Change_dec_32(p,time);
	}
	else
	{
		Value_Change_add_32(p,time);
	}
	WM_InvalidateRect(pMsg->hWin, pRect); //无效化该区域重新刷新
	WM_RestartTimer(pMsg->Data.v, 50); // 重启定时器
}


int Max_Min_32(void *p,s32 Max,s32 Min)
{
	s32 *q = (s32 *)p;
	if((*q)>Max)
	{
		*q = Max;
		return 1; //表示数据溢出
	}
	else if((*q)<Min)
	{
		*q = Min;
		return 1;
	}
	
	return 0; //没有数据溢出
}

