static void Show_Value(void)
{
	char str[10];
	char over;
	s16 *temp;
	
	
	//GAIN
	GUI_SetColor(GUI_WHITE); 		//设置颜色
	GUI_SetTextMode(GUI_TM_TRANS);  //设置透明模式
	GUI_SetFont(GUI_FONT_24B_1); 	//设置字体
	GUI_GotoXY(325,72);
	GUI_DispFloatMin(-(Current_Data->data.GAIN_DATA * step_slider),2);
	
	//START
	GUI_SetColor(GUI_BLACK); 		//设置颜色
	GUI_SetTextMode(GUI_TM_TRANS);  //设置透明模式
	GUI_SetFont(&GUI_Font20_1); 	//设置字体
	temp = &Current_Data->data.START_DATA;
	over = Max_Min(temp,START_DATA_MAX,START_DATA_MIN);
	Current_Data->overflow |= (over<<First_Item);
	snprintf(str,sizeof(str) - 1,"%d",*temp);
	GUI_DispStringHCenterAt(str,150,start_y+2);          //以当前坐标为中心
	
	//STOP
	temp = &Current_Data->data.STOP_DATA;
	over = Max_Min(temp,STOP_DATA_MAX,STOP_DATA_MIN);
	Current_Data->overflow |= (over<<Second_Item);
	snprintf(str,sizeof(str) - 1,"%d",*temp);
	GUI_DispStringHCenterAt(str,150,start_y+44);          //以当前坐标为中心
	
	//STEP
	over = Max_Min(&Current_Data->data.STEP_DATA,STEPS_DATA_MAX,STEPS_DATA_MIN);
	Current_Data->overflow |= (over<<Third_Item);
	snprintf(str,sizeof(str) - 1,"%d",Current_Data->data.STEP_DATA);
	GUI_DispStringHCenterAt(str,150,start_y+86);          //以当前坐标为中心
	
	//CYCSLES
	over = Max_Min_32(&Current_Data->data.CYCSLES_DATA,CYCSLES_MAX,CYCSLES_MIN);
	Current_Data->overflow |= (over<<Four_Item);
	snprintf(str,sizeof(str) - 1,"%d",Current_Data->data.CYCSLES_DATA);
	GUI_DispStringHCenterAt(str,150,204);          //以当前坐标为中心

	//根据更改项来传输数据
	tranrfer_data(Current_Data->change_item);
}

