/*
*******************************************************************************************
* 函 数 名: Value_Change_add
* 功能说明: 增加数值
* 形 参: 数值的地址
* 返 回 值: 无
*******************************************************************************************
*/
void Value_Change_add(void *p,u16 *time)
{
	
	s16 *q = (s16 *)p;
	(*time)++;
	if ((*time) < 20)
	{
		(*q)++;
	}
	else if ((*time) < 40)
	{
		(*q) += 10;
	}
	else if ((*time) < 100)
	{
		(*q) += 100;
	}
	else if((*time) >= 100)
	{
		(*q) += 1000;
	}

}
/*
*******************************************************************************************
* 函 数 名: Value_Change_dec
* 功能说明: 减少数值
* 形 参: 数值的地址
* 返 回 值: 无
*******************************************************************************************
*/
void Value_Change_dec(void *p,u16 *time)
{
	
	s16 *q = (s16 *)p;
	(*time)++;
	if ((*time) < 20)
	{
		(*q)--;
	}
	else if ((*time) < 40)
	{
		(*q) -= 10;
	}
	else if ((*time) < 100)
	{
		(*q) -= 100;
	}
	else if((*time) >= 100)
	{
		(*q) -= 1000;
	}

}

/*
*******************************************************************************************
* 函 数 名: Time_left_dec
* 功能说明: 触摸按钮左，令值减少或减少
* 形 参: 无
* 返 回 值: 无
*******************************************************************************************
*/
void Time_add_dec(s16 *Value,const GUI_RECT * pRect, WM_HTIMER *time,u8 fight,WM_MESSAGE * pMsg)
{
	if((fight%2) ==  0)
	{
		(*Value)--;
	}
	else
	{
		(*Value)++;
	}
	WM_InvalidateRect(pMsg->hWin,pRect);
	(*time) = WM_CreateTimer(pMsg->hWin,fight,200,0);
}

/*
*******************************************************************************************
* 函 数 名: Time_end
* 功能说明: 结束定时器
* 形 参: 无
* 返 回 值: 无
*******************************************************************************************
*/
void Time_end(u16 *count,WM_HTIMER htime)
{
	(*count) = 0;
	WM_DeleteTimer(htime);
}

/*
*******************************************************************************************
* 函 数 名: Time_long_press
* 功能说明: 定时器长按
* 形 参: 无
* 返 回 值: 无
*******************************************************************************************
*/
void Time_long_press(WM_MESSAGE * pMsg,s16 *p,u16 *time,const GUI_RECT * pRect,u8 fight)
{
	if((fight%2) ==  0)
	{
		Value_Change_dec(p,time);
	}
	else
	{
		Value_Change_add(p,time);
	}
	WM_InvalidateRect(pMsg->hWin, pRect); //无效化该区域重新刷新
	WM_RestartTimer(pMsg->Data.v, 50); // 重启定时器
}
