static void Show_Value(void)
{
	char str[10];
	char over;
	s16 *temp;
	s32 date_max;
	
	GUI_SetColor(GUI_BLACK); 		//设置颜色
	GUI_SetTextMode(GUI_TM_TRANS);  //设置透明模式
	GUI_SetFont(&GUI_Font20_1); 	//设置字体
	
	//YEAR
	over = Max_Min(USE_Data.First_data,YEAR_MAX,YEAR_MIN);
	Current_Data->overflow |= (over<<First_Item);
	snprintf(str,sizeof(str) - 1,"%d",*USE_Data.First_data);  //把数值转为字符串
	GUI_DispStringHCenterAt(str,300,41);
	
	//MONTH
	over = Max_Min(USE_Data.Second_data,MONTH_MAX,MONTH_MIN);
	Current_Data->overflow |= (over<<Second_Item);
	snprintf(str,sizeof(str) - 1,"%d",*USE_Data.Second_data);  //把数值转为字符串
	GUI_DispStringHCenterAt(str,300,75);
	
	//DATE
	//判断是否闰年和是否为2月份
	if(Is_Leap_Year(*USE_Data.First_data) &&(*USE_Data.Second_data == 2))
	{
		date_max = 29;
	}
	//2月份平年
	else if(*USE_Data.Second_data == 2)
	{
		date_max = 28;
	}
	//30日的月份
	else if((*USE_Data.Second_data == 4) ||(*USE_Data.Second_data == 6)||
			(*USE_Data.Second_data == 9) ||(*USE_Data.Second_data == 11))
	{
		date_max = 30;
	}
	else
	{
		date_max = 31;
	}
	over = Max_Min(USE_Data.Third_data,date_max,DATE_MIN);
	Current_Data->overflow |= (over<<Third_Item);
	snprintf(str,sizeof(str) - 1,"%d",*USE_Data.Third_data);  //把数值转为字符串
	GUI_DispStringHCenterAt(str,300,109);
	
	//HOUR
	over = Max_Min(USE_Data.Four_data,HOUR_MAX,HOUR_MIN);
	Current_Data->overflow |= (over<<Four_Item);
	snprintf(str,sizeof(str) - 1,"%d",*USE_Data.Four_data);  //把数值转为字符串
	GUI_DispStringHCenterAt(str,300,143);
	
	//MINUTE
	over = Max_Min(USE_Data.Five_data,MINUTE_MAX,MINUTE_MIN);
	Current_Data->overflow |= (over<<Five_Item);
	snprintf(str,sizeof(str) - 1,"%d",*USE_Data.Five_data);  //把数值转为字符串
	GUI_DispStringHCenterAt(str,300,176);
}

