static void Init_data(Time_First_Data *L)
{
	L->Item			=	First_Item;
	L->change_item		=	-1;
	L->Key_count		=	0;
	L->Time_count		=	0;
	
	
	//初始化变量
	USE_Data.First_data 	=	&L->data.YEAR_DATA;
	USE_Data.Second_data	=	&L->data.MONTH_DATA;
	USE_Data.Third_data		=	&L->data.DATE_DATA;
	USE_Data.Four_data 		= 	&L->data.HOUR_DATA;
	USE_Data.Five_data 		= 	&L->data.MINUTE_DATA;
	//没有复选框
	
	/*此处应该更新时间*/
	RTC_Get();//更新时间
	*USE_Data.First_data	=calendar.w_year;
	*USE_Data.Second_data	=calendar.w_month;
	*USE_Data.Third_data	=calendar.w_date;
	*USE_Data.Four_data		=calendar.hour;
	*USE_Data.Five_data		=calendar.min;
}


WM_HWIN Time_First(void) {
	WM_HWIN hWin;
	
	
	Button_flex_2();
	
	Init_data(Time_first);  //初始化数据
	
	Current_Data = Time_first;
	hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
	return hWin;
}

